import { View, Text, ActivityIndicator } from 'react-native';
import React, { useEffect, useState } from 'react';
import { fetchOneUserData } from '../services/api';
import Header from '../components/layout/Header';
import { Feather } from '@expo/vector-icons';

export default function UserDetailScreen({ route }) {
  const { userId } = route.params;
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userData = await fetchOneUserData(userId);
        setUserData(userData);
      } catch (error) {
        console.error('Error al obtener los datos de usuario:', error);
      }
    };

    fetchData();
  }, []);

  if (!userData) {
    return <ActivityIndicator size="large" color="#166D6B" style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }} />;
  }

  console.log(userData);

  return (
    <View>
      <Header />
      <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', padding: 30 }}>
        <Text style={{ color: '#4E4E4E', fontSize: 30, fontWeight: 'bold', maxWidth: 257 }}>Informaci√≥n del usuario</Text>
        <View
          style={{ backgroundColor: '#EFEFEF', borderRadius: 9999, alignItems: 'center', justifyContent: 'center', height: 51, width: 51 }}
        >
          <Feather name="edit" size={19} color="#FFFFFF" />
        </View>
      </View>
    </View>
  );
}
