import { View, Text, ActivityIndicator, ScrollView, Image } from 'react-native';
import React, { useEffect, useState } from 'react';
import { fetchOneUserData } from '../services/api';
import Header from '../components/layout/Header';
import { Feather } from '@expo/vector-icons';
import CustomTextInput from '../components/widgets/CustomTextInput';

export default function UserDetailScreen({ route }) {
  const { userId } = route.params;
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userData = await fetchOneUserData(userId);
        setUserData(userData);
      } catch (error) {
        console.error('Error al obtener los datos de usuario:', error);
      }
    };

    fetchData();
  }, []);

  if (!userData) {
    return <ActivityIndicator size="large" color="#166D6B" style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }} />;
  }

  console.log(userData);

  return (
    <ScrollView style={{ backgroundColor: '#F9FBFB', flex: 1, position: 'relative' }}>
      <View style={{ backgroundColor: '#EDF4D8', height: 688, width: '100%', position: 'absolute', top: 0 }} />
      <Header />
      <View style={{ flexDirection: 'row', justifyContent: 'space-between', padding: 30 }}>
        <Text style={{ color: '#4E4E4E', fontSize: 30, fontWeight: 'bold', maxWidth: 257 }}>Información del usuario</Text>
        <View
          style={{ backgroundColor: '#A2D033', borderRadius: 9999, alignItems: 'center', justifyContent: 'center', height: 51, width: 51 }}
        >
          <Feather name="edit" size={19} color="#FFFFFF" />
        </View>
      </View>
      <View style={{ justifyContent: 'center', alignItems: 'center', marginBottom: 30 }}>
        <Image source={userData.picture} style={{ height: 203, width: 198, borderRadius: 15, borderWidth: 1, borderColor: '#707070' }} />
      </View>
      <View style={{ width: '100%' }}>
        <View style={{ width: '86%', backgroundColor: '#F7F7F7', margin: 'auto', padding: 30, borderRadius: 26, gap: 20 }}>
          <CustomTextInput label="ID" value={userData.id} />
          <CustomTextInput label="Nombres" value={userData.firstName} />
          <CustomTextInput label="Apellidos" value={userData.lastName} />
          <CustomTextInput label="Género" value={userData.gender} />
          <CustomTextInput label="Correo electrónico" value={userData.email} />
          <CustomTextInput label="fecha de nacimiento" value={userData.dateOfBirth} />
          <CustomTextInput label="Telefono" value={userData.phone} />
        </View>
      </View>
    </ScrollView>
  );
}
