import React, { useState } from 'react';
import { View, Text, ScrollView, TextInput, Button, ActivityIndicator, Alert } from 'react-native';
import Header from '../layout/Header';
import { createUserData, fetchUserData } from '../../services/api';

export default function Create() {
  const [newUserData, setNewUserData] = useState({
    title: '',
    firstName: '',
    lastName: '',
    email: '',
    picture: '',
  });

  const [isSaving, setIsSaving] = useState(false);

  const handleFieldChange = (fieldName, value) => {
    setNewUserData({
      ...newUserData,
      [fieldName]: value,
    });
  };

  const handleSave = async () => {
    setIsSaving(true);

    try {
      await createUserData(newUserData);
      Alert.alert('Éxito', 'Usuario creado con éxito');
      setNewUserData({
        title: '',
        firstName: '',
        lastName: '',
        email: '',
        picture: '',
      });
      await fetchUserData();
      setIsSaving(false);
    } catch (error) {
      console.error('Error al crear un nuevo usuario:', error);
      setIsSaving(false);
      Alert.alert('Error', 'Hubo un problema al crear el usuario.');
    }
  };

  return (
    <ScrollView style={{ backgroundColor: '#fefffd', flex: 1 }}>
      <Header />
      <View style={{ padding: 15, margin: 10 }}>
        <TextInput
          placeholder="Título"
          value={newUserData.title}
          onChangeText={(text) => handleFieldChange('title', text)}
        />
        <TextInput
          placeholder="Nombres"
          value={newUserData.firstName}
          onChangeText={(text) => handleFieldChange('firstName', text)}
        />
        <TextInput
          placeholder="Apellidos"
          value={newUserData.lastName}
          onChangeText={(text) => handleFieldChange('lastName', text)}
        />
        <TextInput
          placeholder="Email"
          value={newUserData.email}
          onChangeText={(text) => handleFieldChange('email', text)}
        />
        <TextInput
          placeholder="Foto"
          value={newUserData.picture}
          onChangeText={(text) => handleFieldChange('picture', text)}
        />
        {isSaving ? (
          <ActivityIndicator size="small" color="#166D6B" />
        ) : (
          <Button title="Crear" onPress={handleSave} />
        )}
      </View>
    </ScrollView>
  );
}
